import React,{useEffect,useState} from 'react'
import "./UserInfo.css"
import titleImg from "../../assets/Img/UserInfo/user.png"
import useInfoHook from "../../Hooks/UserInfo/useInfoHook"
import {useRecoilState} from "recoil";
import {pageNumState,showState,SearchState} from "../../stores/state";
import e from 'express';

const UserInfo = () => {

    const [pageNum,setPageNum] = useRecoilState(pageNumState);
    const [show,setShow] = useRecoilState(showState);
    const [searchValue,setSearchValue] = useRecoilState(SearchState);

     const showUser = () => {
        setShow(true)
        setPageNum(0)
    }

    const showAdmin = () => {
        setShow(false)
        setPageNum(0)
    }


    return(
        <div className="UserInfo">
            <div className="userInfoMainBox">
                <div className="userInfo-title">
                    <div className="userInfo-title-titleBox">
                        <img 
                        className="userInfo-title-img"
                        src={titleImg}
                        />
                        <div className="userInfo-title-mainTitle">
                            정보 보기
                        </div>
                    </div>
                    <div className="userInfo-title-options">
                        <div className={show?"user show":"user"} onClick={()=>{showUser()}}>
                            유저                        
                        </div>
                        <div className={show?"admin":"admin show"} onClick={()=>{showAdmin()}}>
                            어드민
                        </div>
                        <div className="search">
                            <select className="search-options"> 
                                <option name="name">이름</option>
                                <option name="email">이메일</option>
                            </select>
                            <input value={searchValue}/>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    )
}

export default UserInfo;
