import React from 'react'
import { useState } from 'react'
import UserInfoApi from '../../assets/Api/UserInfo/UserInfoApi'

const useInfoHook = () => {

    const getUsers = async (pageNum) => {
        const response = await UserInfoApi.getUserList(pageNum);

        return response;
    }

    const [pageNum,setPageNum] = useState(0);
    const [show,setShow] = useState(true);
    const [user,setUser] = useState([]);

    const goLeft = (pageNum) => {
        if(pageNum===0){
            return pageNum
        }
        else{
            setPageNum(pageNum - 1)
        }
    }

    const goRight = (pageNum) => {
        setPageNum(pageNum + 1)
    }

    const showUser = () => {
        setShow(true)
    }

    const showAdmin = () => {
        setShow(false)
    }

    return { 
        getUsers,
        pageNum,
        setPageNum,
        goLeft,
        goRight,
        user,
        setUser,
        show,
        setShow,
        showUser,
        showAdmin

    }
}

export default useInfoHook;
