import React from 'react'
import { useState } from 'react'

const useInfoHook = (text) => {
    const [value,setValue] = useState(text);

    const onChange = e =>{
        setValue(e.target.value);
        console.log(value);
    }
    return {value,onChange}
}

export default useInfoHook;
