import React,{useEffect, useState,useRef,useCallback} from 'react'
import "./Banner.css"
import lol from "../../assets/Img/img.png"
import BannerApi from '../../assets/Api/Banner/BannerApi'

const Banner = () => {

    const [isDragging,setIsDragging] = useState(false);
    const [files, setFiles] = useState([{
        id:Number,
        object:File,
    }]);
    const fileId = useRef(0);

    const dragRef = useRef(null);

    const onChangeFiles = useCallback((e) => {
        let selectFiles = [];
        let tempFiles = files;
        // temp 변수를 이용하여 선택했던 파일들을 담습니다.

        // 드래그 했을 때와 안했을 때 가리키는 파일 배열을 다르게 해줍니다.
        if (e.type === "drop") {
          // 드래그 앤 드롭 했을때
          selectFiles = e.dataTransfer.files;
        } else {
          // "파일 첨부" 버튼을 눌러서 이미지를 선택했을때
          selectFiles = e.target.files;
        }

        for (const file of selectFiles) {
          // 스프레드 연산자를 이용하여 기존에 있던 파일들을 복사하고, 선택했던 파일들을 append 해줍니다.
          tempFiles = [
            ...tempFiles,
            {
              id: fileId.current++, // fileId의 값을 1씩 늘려주면서 각 파일의 고유값으로 사용합니다.
              object: file // object 객체안에 선택했던 파일들의 정보가 담겨있습니다.
            }
          ];
        }

        setFiles(tempFiles);
      }, [files]);

    const handleDragIn = useCallback((e) => {
        e.preventDefault();
        e.stopPropagation();
      }, []);

      const handleDragOut = useCallback((e) => {
        e.preventDefault();
        e.stopPropagation();

        setIsDragging(false);
      }, []);

      const handleDragOver = useCallback((e) => {
        e.preventDefault();
        e.stopPropagation();
        console.log(e);
        // if (e.dataTransfer!.files) {
        //   setIsDragging(true);
        // }
      }, []);

      const handleDrop = useCallback(
        (e)=> {
          e.preventDefault();
          e.stopPropagation();

          onChangeFiles(e);
          setIsDragging(false);
        },
        [onChangeFiles]
      );

      const initDragEvents = useCallback(() => {

        if (dragRef.current !== null) {
          dragRef.current.addEventListener("dragenter", handleDragIn);
          dragRef.current.addEventListener("dragleave", handleDragOut);
          dragRef.current.addEventListener("dragover", handleDragOver);
          dragRef.current.addEventListener("drop", handleDrop);
        }
      }, [handleDragIn, handleDragOut, handleDragOver, handleDrop]);

      const resetDragEvents = useCallback(()=> {

        if (dragRef.current !== null) {
          dragRef.current.removeEventListener("dragenter", handleDragIn);
          dragRef.current.removeEventListener("dragleave", handleDragOut);
          dragRef.current.removeEventListener("dragover", handleDragOver);
          dragRef.current.removeEventListener("drop", handleDrop);
        }
      }, [handleDragIn, handleDragOut, handleDragOver, handleDrop]);


    useEffect( async ()=>{
        const response = await BannerApi.getBannerList();
        initDragEvents();

        return () => resetDragEvents();
    },[initDragEvents, resetDragEvents])
    return(
        <>  
            <div className="Banners">
                <div className="Banner-main">
                    <div className="BannerAdd">
                        <input type="file" id="fileUpload" style={{ display: "none" }} multiple={true}/>
                        <label
                            className={isDragging ? "DragDrop-File-Dragging" : "DragDrop-File"}
                            htmlFor="fileUpload"
                            ref={dragRef}
                         >
                             <div>파일 첨부</div>
                         </label>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                </div>
                <div className="Banner-nav">
                    <div>
                        배너 배너 배너 배너
                    </div>
                </div>
            </div>


        </>
    )
}

export default Banner;
