import React,{useEffect, useState,useRef,useCallback} from 'react'
import "./Banner.css"
import lol from "../../assets/Img/img.png"
import BannerApi from '../../assets/Api/Banner/BannerApi'

const Banner = () => {

    const [isDragging,setIsDragging] = useState(false);
    const [files, setFiles] = useState([]);

    const Form_Data = new FormData();

    const addFile = (e) => {
        setFiles([...files,e.target.files])
    }

    useEffect( async ()=>{
        const response = await BannerApi.getBannerList();

    },[])
    const banners = ()=> {
        for(let i in files[0]){
            return(
                <div>
                    {files[i]}
                </div>
            )
        }

    }
    return(
        <>  
            <div className="Banners">
                <div className="Banner-main">
                    <div className="BannerAdd">
                        <input type="file" id="addFile" style={{ display: "none" }} multiple={true} onChange={addFile}/>
                        <label htmlFor="addFile" className="addlabel">
                            <div>파일을 추가하시려면 클릭하세요</div>
                        </label>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                    <div className="Banner">
                        <img src={lol} alt=""/>
                    </div>
                </div>
                <div className="Banner-nav">
                    <div className="Banner-nav-addBanner">
                    <div className="Banner-nav-addBanner-nav">
                        업로드할 배너 목록
                    </div>
                    <div className="Banner-nav-addBanner-add">

                    {
                        banners()
                    }
                    </div>
                    <div className="Banner-nav-addBanner-btn">
                        추가하기
                    </div>
                    </div>
                </div>
            </div>


        </>
    )
}

export default Banner;
