import React from 'react'
import "./MainPageNation.css"
import {mainPageNumState,mainTotalPageNumState} from "../../stores/mainState"
import {useRecoilState} from "recoil"


const MainPageNation = () => {

    const [pageNum,setPageNum] = useRecoilState(mainPageNumState);
    const [totalPage,setTotalPage] = useRecoilState(mainTotalPageNumState);

    const goLeft = (pageNum) => {
        if(pageNum===0){
            return pageNum
        }else{
            setPageNum(pageNum-1)
        }
    }

    const goRight = (pageNum) => {
        if(pageNum===totalPage){
            return pageNum
        }else{
            setPageNum(pageNum+1)
        }
    }

    return(
        <div className="pageNation">
        <div className="leftBtn" onClick={()=>{goLeft(pageNum)}}>
            앞
        </div>
        <div className="pageNum">
            {pageNum +1}
        </div>
        <div className="rightBtn" onClick={()=>{goRight(pageNum)}}>
            뒤
        </div>
    </div>
    )
}

export default MainPageNation;
