import {
    atom,

} from "recoil";

const pageNumState = atom({
    key:"pageNum",
    default:0,
})

const totalPageState = atom({
    key:"totalPage",
    default:0,
})

const showState = atom({
    key:"show",
    default:true,
})

export {pageNumState,totalPageState,showState};
